import React, { useState } from "react";
import Functionbased from "./Functionbased";
import Classbased from "./Classbased";



function App() {
  const [show, setShow] = useState({ state: false });
  const functionBased = () => {
    show.state ? setShow({ state: false }) : setShow({ state: true });
  };
  return (
    <div className="styling">
      <h1>Styling Using Functional and Class Component</h1>
      <button onClick={() => functionBased()} className="function">
        Div by using Functional Component
      </button>
      {show.state && <Functionbased />}
      <Classbased/>
      </div>
  );
}

export default App;
